(562, 1, 'admin', '/admin/products.Products/add', 'Unknown(add)', '{\"layout\":\"vertical\",\"enable\":\"1\",\"parent_id\":\"2\",\"id\":\"23\",\"en_name\":\"New Type Silicon Carbide Shell and Tube Heat Exchanger with Internal and External Corrosion Protection\",\"image_small\":\"\\/storage\\/default\\/20230526\\/560559bda180d73c6f5ef01f90e71d1b92150769.jpg\"}', '114.217.196.6', 'Mozilla/5.0 (Windows NT 10.0; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/108.0.5359.95 Safari/537.36', 1685087287),
(563, 1, 'admin', '/admin/products.Products/edit', 'Unknown(edit)', '{\"id\":\"23\",\"parent_id\":\"2\",\"layout\":\"vertical\",\"name\":\"\",\"en_name\":\"New Type Silicon Carbide Shell and Tube Heat Exchanger with Internal and External Corrosion Protection\",\"desc\":\"\",\"en_desc\":\"\",\"detail\":\"\",\"en_detail\":\"The new type of internal and external anti-corrosion silicon carbide tubular heat exchanger designed and developed by our company adopts double-sided molded PFA tube sheet, so that the tube sheet can be anti-corrosion both inside and outside. At the same time, in the shell side and inside the head of the heat exchanger, the anti-corrosion coating process of lining glass and fluoroplastics can be carried out. In this way, customers can not only use conventional cold sources or heat sources, but also use corrosive cold sources or heat sources, which greatly reduces the use cost of customers.\",\"image_small\":\"\\/storage\\/default\\/20230526\\/560559bda180d73c6f5ef01f90e71d1b92150769.jpg\",\"image_mid\":\"\",\"image_large\":\"\",\"carousel\":[],\"enable\":\"1\"}', '114.217.196.6', 'Mozilla/5.0 (Windows NT 10.0; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/108.0.5359.95 Safari/537.36', 1685087314),
